INSERT INTO `email_text` (`id`,`description`,`sender`,`recipient`,`bcc`,`subject`,`emailbody`)
VALUES
	('temporary_password','This email gets sent to people when they request a temporary password from the site.','##WEBSITE_EMAIL##','##EMAIL##','','[Oregon CTE Pathways] Temporary Password','Click on the link below to log in to your account temporarily. You will need to change your password as soon as you log in, as this link will only work once.\r\n\r\nThis email was sent because you or someone else requested a temporary password from the Oregon CT Pathways website. If you have received this email in error, please ignore it. Your current password will still be valid.\r\n\r\n##LOGIN_LINK##\r\n\r\nIf you cannot click the link above for any reason, you can also log into the site using the temporary password below.\r\n\r\nhttp://oregon.ctepathways.org\r\n##EMAIL##\r\n##PASSWORD##\r\n\r\nSincerely,\r\nOregon Career Pathways\r\nWeb Tool User Support\r\n\r\n##WEBSITE_EMAIL##'),
	('help_request','This email is sent to help@ctepathways.org when a user fills out the help form on the website.','##EMAIL##','helpdesk@ctepathways.org','','[Oregon CTE Pathways] ##SUBJECT##','##BODY##'),
	('account_approved','Sent to the user when someone approves their account request. Contains a temporary password.','##WEBSITE_EMAIL##','##EMAIL##','','[Oregon CTE Pathways] Account Request Approved','Welcome! Your account for the Career Pathways Web Tool has been activated. This email contains a temporary password you can use to log in to your account. Once logged in, you will need to change your password, as this link will work only once.\r\n\r\nYou can log in by clicking the link below, or you can visit http://oregon.ctepathways.org and log in using the password below. For best results while working in the Web Tool, please use FireFox 2.0 or greater.\r\n\r\n##LOGIN_LINK##\r\n\r\nEmail: ##EMAIL##\r\nPassword: ##PASSWORD##\r\n\r\nOnce logged in, we encourage you to visit our Getting Started Tutorial for complete step-by-step instructions and details for all Web Tool features. Links to this are found in the Site News section of the Web Tool. Within the tutorial you will also find a short video demonstration to orient you with some of the more dynamic drawing features.\r\n\r\nPlease do not hesitate to contact us for training and technical assistance as you get started.\r\n\r\nSincerely,\r\nOregon Career Pathways\r\nWeb Tool User Support\r\n\r\n##WEBSITE_EMAIL##\r\n'),
	('account_request','Sent to school admins when someone fills out the account request form.','##WEBSITE_EMAIL##','##RECIPIENTS##','','[Oregon CTE Pathways] New Account Request','Hello,\r\n\r\nThis is an automated email sent to you because you are listed as a \"School Admin\" for your school on the Oregon Career Pathways website.\r\n\r\nThe person below has requested an account at your school. As a \"School Admin\" you can approve this person\'s request. Please do not approve the request unless you know this person, since they will have the ability to edit any drawing at your school. Review the details for this person, then click on the link below to create their account.\r\n\r\n##USER_INFO##\r\n\r\n##APPROVE_LINK##\r\n\r\nSincerely,\r\nOregon Career Pathways\r\nWeb Tool User Support\r\n\r\n##WEBSITE_EMAIL##\r\n'),
	('account_request_rt','Sent to RT when someone fills out the account request form.','helpdesk@ctepathways.org','helpdesk@ctepathways.org','','[Oregon CTE Pathways] New Account Request','Hello,\r\n\r\nThe person below has requested an account on the Career Pathways Web Tool. If this user has requested an account at an existing school, the School Admin users for that school have already received this message. Review the details for this person, then click on the link below to approve and create their account.\r\n\r\n##USER_INFO##\r\n\r\n##APPROVE_LINK##\r\n\r\nSincerely,\r\nOregon Career Pathways\r\nWeb Tool User Support\r\n\r\n##WEBSITE_EMAIL##\r\n\r\n');
